<!-- 评分组件模板 -->
<template>
  <div>
    <div class="rater-box" :style="{'width': initW, 'height': initH}">
      <div class="rater-item" v-for="(item, index) in stars">
        <div class="star-box" @click="clickStar(index)" :style="{'width': starW, 'height': starH}">
          <img class="small-icon" :src="initScore > index ? item.light : item.gray"></img>
        </div>
        <div :class="['tips', initScore > index ? 'active' : '']" v-if="item.tips">
          {{item.tips}}
        </div>
      </div>
    </div>
  </div>
</template>
<script>
  export default {
    name: 'raterVue',
    props: {
      initW: {
        type: String,
        default: '100%'
      },
      initH: {
        type: String,
        default: '100%'
      },
      score: {
        type: Number,
        default: 0
      },
      onlyShow: {
        type: Boolean,
        default: false
      },
      starW: {
        type: String,
        default: '29px'
      },
      starH: {
        type: String,
        default: '28px'
      },
      stars: {
        type: Array,
        default: function () {
          return [
          {
            light: 'data:image/png;base64,',
            gray: 'data:image/png;base64,',
            tips: '极差'
          },{
            light: 'data:image/png;base64,',
            gray: 'data:image/png;base64,',
            tips: '差'
          },{
            light: 'data:image/png;base64,',
            gray: 'data:image/png;base64,',
            tips: '一般'
          },{
            light: 'data:image/png;base64,',
            gray: 'data:image/png;base64,',
            tips: '好'
          },{
            light: 'data:image/png;base64,',
            gray: 'data:image/png;base64,',
            tips: '极好'
          }
        ]
        }
      }
    },
    data () {
      return {
        initScore: 0
      }
    },
    methods: {
      clickStar: function (index) {
        if (this.onlyShow) return
          if (this.initScore == index + 1) {
            this.initScore = index
          } else {
            this.initScore = index + 1
          }
        this.$emit('change', index + 1)
      }
    },
    mounted () {
      console.log(11111)
      this.initScore = this.score
      console.log(this.initScore, 'this.initScore')
      console.log(this.stars, 'this.stars')
    }
  }
</script>
<style scoped>
  .rater-box {
    width: 100%;
    display: flex;
    justify-content: space-around;
    align-items:center;
    background-color: #fff;
  }
  .rater-item {
    vertical-align: middle;
    display: inline-block;
  }
  .star-box {
    position: relative;
  }
  .small-icon {
    width: 100%;
    height: 100%;
    vertical-align: top;
  }
  .tips {
    color: #A6A6A6;
    font-size: 12px;
    text-align: center;
    line-height: 18px;
  }
  .tips.active {
    color: #816F68;
  }
</style>